sql.if:
(e.signNum = #{keyword}
OR INSTR(e.title, #{keyword}) > 0
OR INSTR(name, #{keyword}) > 0
OR (TO_CHAR(dr.reg_date, 'YYYY-MM-DD') = #{keyword}
OR
TO_CHAR(dr.reg_date, 'YYYYMMDD') = #{keyword})
)

mapper.update:
UPDATE SET reg_date = sysdate, state='승인' FROM
f_approval s_approval t_approval dual
WHERE signNum = #{signNum}
