mapper.update:
UPDATE notice SET subject = #{subject},
	content = #{content}, notice = #{notice}
WHERE num = #{num}

mapper.select:
SELECT fileNum, num, saveFilename, originalFilename, fileSize
FROM noticeFile
	WHERE num = #{num}

	SELECT fileNum, num, saveFilename, originalFilename, fileSize
	FROM noticeFile
    WHERE fileNum = #{fileNum}
