mapper.update:
UPDATE Dayoff 
SET workDays = #{workDays}, leftQty = #{leftQty}, 
   reqDays= #{reqDays}, workDays=#{workDays}, totalQty = #{totalQty} 
WHERE empNo = #{empNo}

mapper.select:
   		SELECT gainQty as qty, gainDate as reg_date, '연차 획득' as content FROM gainDayoff WHERE empNo = #{empNo}
		UNION ALL
		SELECT gainQty as qty, gainDate as reg_date, content FROM gainSpecialVacation WHERE empNo = #{empNo}
		UNION ALL
		SELECT (endDate-startDate) as qty, reg_date, vacationType as content FROM vacation v
		JOIN E_DOCUMENT e ON e.signNum = v.signNum
		JOIN doc_record dr ON dr.signNum = v.signNum
		WHERE empNo= #{empNo};

   		SELECT NVL(SUM(endDate-startDate),0) from vacation v
		JOIN e_document e ON v.signNum = e.signNum
		where empNo= #{empNo}
